        }

        /* Icons */
        [fill="red"],
        [fill="#F00"],
        [fill="#FF0000"] {
            fill: @accent-color !important;
        }

        [fill="white"] {
            fill: if(@lookup =latte, @base, @text) !important;
        }

        yt-icon.ytd-logo [fill="white"] {
            fill: @crust !important;
        }

        .yt-spec-icon-shape {
            [fill="#FAFAFA"],
            [fill="#000"],
            [fill="#fff"],
            [fill="#FFF"],
            [fill="#FFFFFF"] {
                fill: @crust !important;
            }
