    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    --ctp-rosewater: #rgbify(@rosewater) [];
    --ctp-flamingo: #rgbify(@flamingo) [];
    --ctp-pink: #rgbify(@pink) [];
    --ctp-mauve: #rgbify(@mauve) [];
    --ctp-red: #rgbify(@red) [];
    --ctp-maroon: #rgbify(@maroon) [];
    --ctp-peach: #rgbify(@peach) [];
    --ctp-yellow: #rgbify(@yellow) [];
    --ctp-green: #rgbify(@green) [];
    --ctp-teal: #rgbify(@teal) [];
    --ctp-sky: #rgbify(@sky) [];
    --ctp-sapphire: #rgbify(@sapphire) [];
    --ctp-blue: #rgbify(@blue) [];
    --ctp-lavender: #rgbify(@lavender) [];
    --ctp-text: #rgbify(@text) [];
    --ctp-subtext1: #rgbify(@subtext1) [];
    --ctp-subtext0: #rgbify(@subtext0) [];
    --ctp-overlay2: #rgbify(@overlay2) [];
    --ctp-overlay1: #rgbify(@overlay1) [];
    --ctp-overlay0: #rgbify(@overlay0) [];
    --ctp-surface2: #rgbify(@surface2) [];
    --ctp-surface1: #rgbify(@surface1) [];
    --ctp-surface0: #rgbify(@surface0) [];
    --ctp-base: #rgbify(@base) [];
    --ctp-mantle: #rgbify(@mantle) [];
    --ctp-crust: #rgbify(@crust) [];

    color-scheme: if(@lookup =latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    --bard-color-primary: @accent-color;
    --bard-color-primary-background: @base;
    --bard-color-primary-opacity-08: fade(@accent-color, 8%);
    --bard-color-outline: @overlay2;
    --bard-color-on-primary: darken(@accent-color, 10%);
    --bard-color-on-primary-container: @text;
    --bard-color-on-selected-conversation-metadata-v2: @text;
    --bard-color-surface-container: @mantle;
    --bard-color-on-surface-opacity-38: @surface2 !important;
    --bard-color-surface-container-low: lighten(@mantle, 2%);
    --bard-color-surface-container-highest: lighten(@base, 5%);
    --bard-color-surface-container-high: @surface0;
    --bard-color-response-container-flipped-background: @surface0;
    --bard-color-inverse-on-surface: darken(@mantle, 1%);
    --bard-color-sidenav-mobile-controls: @text;
    --bard-color-outline-variant: @subtext0;
    --bard-color-main-container-background: @base;
    --bard-color-main-container-background-rgb: #rgbify(@base) [];
    --bard-color-window-container-background: @base;
    --bard-color-response-container-background: @base;
    --bard-color-response-container-background-rgb: #rgbify(@base) [];
    --bard-color-selected-conversation-metadata-v2: fade(@accent-color, 50%);
